  position: relative;
  width: 50%;
  height: 85%;
  border-radius: 20px;
  overflow: hidden;
`;

const TheCard = styled.div`
  position: absolute;
  width: 100%;
  height: 100%;
  transform-style: preserve-3d;
  transition: all 0.5s ease;
`;

const TheBack = styled.div`
  padding: 20px;
  background-color: #e8ecde;
`;

const TheBackText = styled.p`
  margin-top: 10px;
  margin-bottom: 10px;
`;

const RightSideHover = styled.div`
  &:hover {
    transform: rotateY(180deg);
  }
`;

const RightSideImage = styled.img`
  width: 100%;
  height: 100%;
`;

const TheFront = styled.div`
  position: absolute;
  width: 100%;
  height: 100%;
  backface-visibility: hidden;
  background: #ffc728;
  color: #333;
`;

const TheBackFace = styled.div`
  position: absolute;
  width: 100%;
  height: 100%;
  backface-visibility: hidden;
  background: #fafafa;
  color: #333;
  transform: rotateY(180deg);
`;

